public class DecimalDivision {
    public static void main(String[] args) {
        double dividend = 1.25;
        int divisor = 4;

        // Convert the dividend to a whole number by moving the decimal point
        double wholeDividend = dividend * Math.pow(10, -dividend.toString().indexOf('.') + 1);
        int countDecimals = dividend.toString().length() - dividend.toString().indexOf('.') - 1;

        // Perform division as if it were with whole numbers
        int quotient = (int) (wholeDividend / divisor);

        // Place the decimal point back in the quotient
        double result = quotient * Math.pow(10, countDecimals);

        System.out.println("Result: " + result);
    }
}
