            this.ownerName = ownerName;
            this.balance = initialBalance;
        }

        void depositMoney(double amount) {
            if (amount > 0) {
                balance += amount;
                System.out.println(ownerName + " deposited $" + amount);
            } else {
                System.out.println("Invalid deposit amount.");
            }
