        this.expirationDate = expirationDate;
    }

    public void revokeOffer() {
        isRevoked = true;
    }

    public boolean isExpiredOrRevoked() {
        return (Calendar.getInstance().getTime().after(expirationDate)) || isRevoked;
    }
}
