        if (!isValidRequest(request)) {
            rejectRequest(request);
            return false;
        }

        // Handle any potential overdraft by borrowing or refusing
        if (isOverdraft(request.getSender())) {
            borrowFunds(request.getSender());
            processRequest(request);
        } else {
            rejectRequest(request);
        }
    }
}
