        if (isWithinScopeOfEmployment()) {
            // Action within scope of employment, R&K liable
        } else {
            // Action outside scope, individual liability
        }
    }

    private boolean isWithinScopeOfEmployment() {
        return this.isWithinScopeOfEmployment;
    }
}
