        boolean materialLegality = checkMaterialLegality(agreement.getMaterials());

        if (serviceLegality && !materialLegality) {
            return true; // Only enforce services
        } else if (!serviceLegality && materialLegality) {
            return false; // Enforce materials, not services
        }
        return false; // Both illegal or both legal
    }
}
