    }

    public boolean receiveAndValidate(String confirmation, Date dateReceived) {
        if (isReasonableTime(dateReceived, dateSent)) {
            return true; // Confirmation valid within 10 days
        } else {
            return false; // Outside reasonable time
        }
    }
}
