        } else {
            System.out.println("Money damages will suffice.");
        }
    }

    private boolean isMonetaryCompensationInadequate(String contractDetails) {
        // Logic to determine if the breach involves unique goods or services
        return contractDetails.contains("unique painting") || contractDetails.contains("fungible commodity");
    }
}
