        this.h = h;
        this.k = k;
        this.l = l;
    }

    public double getAxialInterceptA() {
        return (double)h; // A = h * a
    }

    public double getAxialInterceptB() {
        return Double.POSITIVE_INFINITY; // B = ∞b
    }

    public double getAxialInterceptC() {
        return (double)l; // C = l * c
    }
}
