    public boolean intersectsXAxis(double a) {
        return h != 0; // A = h * a
    }

    public boolean parallelsYAxis() {
        return k == 0; // B = ∞b
    }

    public boolean intersectsZAxis(double c) {
        return l != 0; // C = l * c
    }
}
